const initState = {
    families: [
        {pin: "0001", lastName: "Loudermilk", numOfMem: "4", guests: "1"},
        {pin: "0002", lastName: "Dodd", numOfMem: "3", guests: "4"}
    ]
}

const familyReducer = (state = initState, action) => {
    switch (action.type) {
        case 'FAMILY_UPDATE':
            console.log('updated family: ', action.family);
            return state;
        case 'FAMILY_UPDATE_ERR':
            console.log('updating family error: ', action.err);
            return state;
        default:
            return state;
    } 
}

export default familyReducer
